                    list(
                        `name`="ivplot", 
                        `type`="number"),
                    list(
                        `name`="iv2plot", 
                        `type`="number"),
                    list(
                        `name`="dvplot", 
                        `type`="number")),
                clearWith=NULL))}))

BrawSimBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "BrawSimBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "BrawStats",
                name = "BrawSim",
                version = c(1,0,0),
                options = options,
                results = BrawSimResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'na')
        }))

#' BrawStats:Simulate Data
#'
#' 
#' @param DVname .
#' @param DVtype .
#' @param DVmu .
#' @param DVsd .
#' @param DVskew .
#' @param DVkurt .
#' @param DVnlevs .
#' @param DViqr .
#' @param DVncats .
#' @param DVprops .
#' @param IVname .
#' @param IVtype .
#' @param IVmu .
#' @param IVsd .
#' @param IVskew .
#' @param IVkurt .
#' @param IVnlevs .
#' @param IViqr .
#' @param IVncats .
#' @param IVprops .
#' @param IV2on .
#' @param IV2name .
#' @param IV2type .
#' @param IV2mu .
#' @param IV2sd .
#' @param IV2skew .
#' @param IV2kurt .
#' @param IV2nlevs .
#' @param IV2iqr .
#' @param IV2ncats .
#' @param IV2props .
#' @param EffectSize1 .
#' @param EffectSize2 .
#' @param EffectSize3 .
#' @param EffectSize12 .
#' @param WorldOn .
#' @param WorldPDF .
#' @param WorldRZ .
#' @param Worldk .
#' @param WorldNullP .
#' @param SampleSize .
#' @param SampleMethod .
#' @param SampleUsage1 .
#' @param SampleUsage2 .
#' @param Dependence .
#' @param Outliers .
#' @param Cheating .
#' @param CheatingAttempts .
#' @param Welch .
#' @param Transform .
#' @param multipleDoingNull .
#' @param exploreNPoints .
#' @param exploreMaxN .
#' @param exploreDoingNull .
#' @param exploreNscale .
#' @param showHypothesisBtn .
#' @param showSampleBtn .
#' @param makeSampleBtn .
#' @param numberSamples .
#' @param numberExplores .
#' @param makeMultipleBtn .
#' @param showMultipleBtn .
#' @param makeExploreBtn .
#' @param showExploreBtn .
#' @param makeCopyBtn .
#' @param copyValues .
#' @param appendValues .
#' @param showHypothesis .
#' @param showSample .
#' @param showInfer .
#' @param showMultiple .
#' @param typeExplore .
#' @param showExplore .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$graphPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$reportPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$debug} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$sendValues} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$tableStore} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$tableStore$asDF}
#'
#' \code{as.data.frame(results$tableStore)}
#'
#' @export
BrawSim <- function(
    DVname = "DV",
    DVtype = "Interval",
    DVmu = 0,
    DVsd = 1,
    DVskew = 0,
    DVkurt = 3,
    DVnlevs = 7,
    DViqr = 4,
    DVncats = 2,
    DVprops = "1,1",
    IVname = "IV",
    IVtype = "Interval",
    IVmu = 0,
    IVsd = 1,
    IVskew = 0,
    IVkurt = 3,
    IVnlevs = 7,
    IViqr = 4,
    IVncats = 2,
    IVprops = "1,1",
    IV2on,
    IV2name = "IV2",
    IV2type = "Interval",
    IV2mu = 0,
    IV2sd = 1,
    IV2skew = 0,
    IV2kurt = 3,
    IV2nlevs = 7,
    IV2iqr = 4,
    IV2ncats = 2,
    IV2props = "1,1",
    EffectSize1 = 0.3,
    EffectSize2 = 0.3,
    EffectSize3 = 0.3,
    EffectSize12 = 0.3,
    WorldOn = FALSE,
    WorldPDF = "Single",
    WorldRZ,
    Worldk = 0.3,
    WorldNullP = 0.5,
    SampleSize = 42,
    SampleMethod = "Random",
    SampleUsage1 = "Between",
    SampleUsage2 = "Between",
    Dependence = 0,
    Outliers = 0,
    Cheating = "None",
    CheatingAttempts = 5,
    Welch = FALSE,
    Transform = "None",
    multipleDoingNull = FALSE,
    exploreNPoints = 13,
    exploreMaxN = 250,
    exploreDoingNull = FALSE,
    exploreNscale = FALSE,
    showHypothesisBtn,
    showSampleBtn = FALSE,
    makeSampleBtn = FALSE,
    numberSamples = 100,
    numberExplores = 10,
    makeMultipleBtn,
    showMultipleBtn,
    makeExploreBtn,
    showExploreBtn,
    makeCopyBtn,
    copyValues,
    appendValues,
    showHypothesis = "Hypothesis",
    showSample = "Sample",
    showInfer = "Basic",
    showMultiple = "Basic",
    typeExplore = "n",
    showExplore = "r") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("BrawSim requires jmvcore to be installed (restart may be required)")


    options <- BrawSimOptions$new(
        DVname = DVname,
        DVtype = DVtype,
        DVmu = DVmu,
        DVsd = DVsd,
        DVskew = DVskew,
        DVkurt = DVkurt,
        DVnlevs = DVnlevs,
        DViqr = DViqr,
        DVncats = DVncats,
        DVprops = DVprops,
        IVname = IVname,
        IVtype = IVtype,
        IVmu = IVmu,
        IVsd = IVsd,
        IVskew = IVskew,
        IVkurt = IVkurt,
        IVnlevs = IVnlevs,
        IViqr = IViqr,
        IVncats = IVncats,
        IVprops = IVprops,
        IV2on = IV2on,
        IV2name = IV2name,
        IV2type = IV2type,
        IV2mu = IV2mu,
        IV2sd = IV2sd,
        IV2skew = IV2skew,
        IV2kurt = IV2kurt,
        IV2nlevs = IV2nlevs,
        IV2iqr = IV2iqr,
        IV2ncats = IV2ncats,
        IV2props = IV2props,
        EffectSize1 = EffectSize1,
        EffectSize2 = EffectSize2,
        EffectSize3 = EffectSize3,
        EffectSize12 = EffectSize12,
        WorldOn = WorldOn,
        WorldPDF = WorldPDF,
        WorldRZ = WorldRZ,
        Worldk = Worldk,
        WorldNullP = WorldNullP,
        SampleSize = SampleSize,
        SampleMethod = SampleMethod,
        SampleUsage1 = SampleUsage1,
        SampleUsage2 = SampleUsage2,
        Dependence = Dependence,
        Outliers = Outliers,
        Cheating = Cheating,
        CheatingAttempts = CheatingAttempts,
        Welch = Welch,
        Transform = Transform,
        multipleDoingNull = multipleDoingNull,
        exploreNPoints = exploreNPoints,
        exploreMaxN = exploreMaxN,
        exploreDoingNull = exploreDoingNull,
        exploreNscale = exploreNscale,
        showHypothesisBtn = showHypothesisBtn,
        showSampleBtn = showSampleBtn,
        makeSampleBtn = makeSampleBtn,
        numberSamples = numberSamples,
        numberExplores = numberExplores,
        makeMultipleBtn = makeMultipleBtn,
        showMultipleBtn = showMultipleBtn,
        makeExploreBtn = makeExploreBtn,
        showExploreBtn = showExploreBtn,
        makeCopyBtn = makeCopyBtn,
        copyValues = copyValues,
        appendValues = appendValues,
        showHypothesis = showHypothesis,
        showSample = showSample,
        showInfer = showInfer,
        showMultiple = showMultiple,
        typeExplore = typeExplore,
        showExplore = showExplore)

    analysis <- BrawSimClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

