#' BrawStats:Linear Models
#'
#' 
#' @param data .
#' @param IV .
#' @param DV .
#' @param inferWhich .
#' @param whichR .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$graphPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$reportPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$reportTable} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$reportTable$asDF}
#'
#' \code{as.data.frame(results$reportTable)}
#'
#' @export
BrawLM <- function(
    data,
    IV,
    DV,
    inferWhich = "r",
    whichR = "Unique") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("BrawLM requires jmvcore to be installed (restart may be required)")

    if ( ! missing(IV)) IV <- jmvcore::resolveQuo(jmvcore::enquo(IV))
    if ( ! missing(DV)) DV <- jmvcore::resolveQuo(jmvcore::enquo(DV))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(IV), IV, NULL),
            `if`( ! missing(DV), DV, NULL))


    options <- BrawLMOptions$new(
        IV = IV,
        DV = DV,
        inferWhich = inferWhich,
        whichR = whichR)

    analysis <- BrawLMClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

